//Rating with box components
import './App.css';
import React, { useState} from 'react';

function App2() {
const [data, setData] = useState(null);
const [print, setPrint] = useState(false);


function getData(val) {
  setData(val.target.value)
  setPrint(false)
  console.log(val.target.value) 
}

const handleSubmit = event => {
  event.preventDefault();
  alert('You have submitted the form.')
}


  return (
    <><div className='mov'>
      <h2> Leave your thoughts below!</h2>
      <form onSubmit={handleSubmit}>
        <textarea required className='bottom'
          placeholder="Thoughts on this movie?" onChange={getData} />
        <div className='bottom'>
          {print ?
            <p className='mov'> {data}</p>
            : null}
          <button type='submit' onClick={() => setPrint(true)}>
            Submit  </button>
          <input type="button" value="Place Order" onclick="Submit()"></input>
        </div>
      </form>

    </div>
    <table id="table" class="table table-dark">
        <tr class="test">
          <th class=".col">Name</th>
          <th class=".col col-sm-8">Order</th>
          <th class=".col">Time</th>
          <th class=".col">Date</th>
          <th class=".col col-sm-4">Edit/delete</th>
        </tr>
      </table></>

  );
};




export default App2;
